mapper.select:
SELECT
    id                            AS id,
    waybill_code                  AS waybillCode,
    template_code                 AS templateCode,
    action_code                   AS actionCode,
    charge_item_code              AS chargeItemCode,
    charge_item_name              AS chargeItemName,
    charge_item_number_unit_code  AS chargeItemNumberUnitCode,
    charge_item_number_unit_name  AS chargeItemNumberUnitName,
    charge_item_price_unit_code   AS chargeItemPriceUnitCode,
    charge_item_price_unit_name   AS chargeItemPriceUnitName,
    charge_item_amounts_unit_code AS chargeItemAmountsUnitCode,
    charge_item_amounts_unit_name AS chargeItemAmountsUnitName,
    usage_desc                    AS usageDesc,
    visible_config                AS visibleConfig,
    description                   AS description,
    remark                        AS remark,
    disabled                      AS disabled,
    deleted                       AS deleted,
    module_code                   AS moduleCode,
    creator_user_code             AS creatorUserCode,
    creator_username              AS creatorUsername,
    create_time                   AS createTime,
    update_user_code              AS updateUserCode,
    update_username               AS updateUsername,
    update_time                   AS updateTime,
    ip                            AS ip,
    operator_longitude            AS operatorLongitude,
    operator_latitude             AS operatorLatitude,
    owner_user_code               AS ownerUserCode,
    owner_org_code                AS ownerOrgCode,
    owner_org_name                AS ownerOrgName,
    synchronous_id                AS synchronousId
FROM
    waybill_action_charge_item_relation


    AND id = #{id}



    AND waybill_code = #{waybillCode}



    AND template_code = #{templateCode}



    AND action_code = #{actionCode}



    AND charge_item_code = #{chargeItemCode}



    AND charge_item_name = #{chargeItemName}



    AND charge_item_number_unit_code = #{chargeItemNumberUnitCode}



    AND charge_item_number_unit_name = #{chargeItemNumberUnitName}



    AND charge_item_price_unit_code = #{chargeItemPriceUnitCode}



    AND charge_item_price_unit_name = #{chargeItemPriceUnitName}



    AND charge_item_amounts_unit_code = #{chargeItemAmountsUnitCode}



    AND charge_item_amounts_unit_name = #{chargeItemAmountsUnitName}



    AND usage_desc = #{usageDesc}



    AND visible_config = #{visibleConfig}



    AND description = #{description}



    AND remark = #{remark}



    AND disabled = #{disabled}



    AND deleted = #{deleted}



    AND module_code = #{moduleCode}



    AND creator_user_code = #{creatorUserCode}



    AND creator_username = #{creatorUsername}



    AND create_time = #{createTime}



    AND update_user_code = #{updateUserCode}



    AND update_username = #{updateUsername}



    AND update_time = #{updateTime}



    AND ip = #{ip}



    AND operator_longitude = #{operatorLongitude}



    AND operator_latitude = #{operatorLatitude}



    AND owner_user_code = #{ownerUserCode}



    AND owner_org_code = #{ownerOrgCode}



    AND owner_org_name = #{ownerOrgName}



    AND synchronous_id = #{synchronousId}
