mapper.select:
SELECT
    id            AS id,
    event_code    AS eventCode,
    consumer_name AS consumerName,
    topic         AS topic,
    message       AS message,
    status        AS status,
    failed_reason AS failedReason,
    create_time   AS createTime,
    update_time   AS updateTime
FROM
    waybill_comsumer_event


    AND id = #{id}



    AND event_code = #{eventCode}



    AND consumer_name = #{consumerName}



    AND topic = #{topic}



    AND message = #{message}



    AND status = #{status}



    AND failed_reason = #{failedReason}



    AND create_time = #{createTime}



    AND update_time = #{updateTime}
