mapper.select:
SELECT
    id                              AS id,
    code                            AS code,
    payment_no                      AS paymentNo,
    waybill_code                    AS waybillCode,
    waybill_no                      AS waybillNo,
    billing_class                   AS billingClass,
    last_print_no                   AS lastPrintNo,
    confirmed_tran_fee              AS confirmedTranFee,
    tran_fee_unit_code              AS tranFeeUnitCode,
    confirmed_driving_fee           AS confirmedDrivingFee,
    confirmed_driving_fee_unit_code AS confirmedDrivingFeeUnitCode,
    confirmed_total_fee             AS confirmedTotalFee,
    total_fee_unit_code             AS totalFeeUnitCode,
    total_fee_unit_name             AS totalFeeUnitName,
    actual_fee                      AS actualFee,
    actual_fee_unit_code            AS actualFeeUnitCode,
    actual_fee_unit_name            AS actualFeeUnitName,
    pay_method                      AS payMethod,
    pay_status                      AS payStatus,
    pay_request_org_code            AS payRequestOrgCode,
    pay_request_org_name            AS payRequestOrgName,
    pay_request_user_code           AS payRequestUserCode,
    pay_request_user_full_name      AS payRequestUserFullName,
    pay_request_time                AS payRequestTime,
    payer_org_code                  AS payerOrgCode,
    payer_org_name                  AS payerOrgName,
    payer_op_code                   AS payerOpCode,
    payer_op_full_name              AS payerOpFullName,
    payer_op_time                   AS payerOpTime,
    payee_user_code                 AS payeeUserCode,
    payee_user_full_name            AS payeeUserFullName,
    payee_bank_name                 AS payeeBankName,
    payee_bank_branch_name          AS payeeBankBranchName,
    payee_bank_account_full_name    AS payeeBankAccountFullName,
    payee_bank_account_no           AS payeeBankAccountNo,
    description                     AS description,
    remark                          AS remark,
    disabled                        AS disabled,
    deleted                         AS deleted,
    module_code                     AS moduleCode,
    creator_user_code               AS creatorUserCode,
    creator_username                AS creatorUsername,
    create_time                     AS createTime,
    update_user_code                AS updateUserCode,
    update_username                 AS updateUsername,
    update_time                     AS updateTime,
    ip                              AS ip,
    operator_longitude              AS operatorLongitude,
    operator_latitude               AS operatorLatitude,
    owner_user_code                 AS ownerUserCode,
    owner_org_code                  AS ownerOrgCode,
    owner_org_name                  AS ownerOrgName,
    synchronous_id                  AS synchronousId
FROM
    waybill_paybill


    AND id = #{id}



    AND code = #{code}



    AND payment_no = #{paymentNo}



    AND waybill_code = #{waybillCode}



    AND waybill_no = #{waybillNo}



    AND billing_class = #{billingClass}



    AND last_print_no = #{lastPrintNo}



    AND confirmed_tran_fee = #{confirmedTranFee}



    AND tran_fee_unit_code = #{tranFeeUnitCode}



    AND confirmed_driving_fee = #{confirmedDrivingFee}



    AND confirmed_driving_fee_unit_code = #{confirmedDrivingFeeUnitCode}



    AND confirmed_total_fee = #{confirmedTotalFee}



    AND total_fee_unit_code = #{totalFeeUnitCode}



    AND total_fee_unit_name = #{totalFeeUnitName}



    AND actual_fee = #{actualFee}



    AND actual_fee_unit_code = #{actualFeeUnitCode}



    AND actual_fee_unit_name = #{actualFeeUnitName}



    AND pay_method = #{payMethod}



    AND pay_status = #{payStatus}



    AND pay_request_org_code = #{payRequestOrgCode}



    AND pay_request_org_name = #{payRequestOrgName}



    AND pay_request_user_code = #{payRequestUserCode}



    AND pay_request_user_full_name = #{payRequestUserFullName}



    AND pay_request_time = #{payRequestTime}



    AND payer_org_code = #{payerOrgCode}



    AND payer_org_name = #{payerOrgName}



    AND payer_op_code = #{payerOpCode}



    AND payer_op_full_name = #{payerOpFullName}



    AND payer_op_time = #{payerOpTime}



    AND payee_user_code = #{payeeUserCode}



    AND payee_user_full_name = #{payeeUserFullName}



    AND payee_bank_name = #{payeeBankName}



    AND payee_bank_branch_name = #{payeeBankBranchName}



    AND payee_bank_account_full_name = #{payeeBankAccountFullName}



    AND payee_bank_account_no = #{payeeBankAccountNo}



    AND description = #{description}



    AND remark = #{remark}



    AND disabled = #{disabled}



    AND deleted = #{deleted}



    AND module_code = #{moduleCode}



    AND creator_user_code = #{creatorUserCode}



    AND creator_username = #{creatorUsername}



    AND create_time = #{createTime}



    AND update_user_code = #{updateUserCode}



    AND update_username = #{updateUsername}



    AND update_time = #{updateTime}



    AND ip = #{ip}



    AND operator_longitude = #{operatorLongitude}



    AND operator_latitude = #{operatorLatitude}



    AND owner_user_code = #{ownerUserCode}



    AND owner_org_code = #{ownerOrgCode}



    AND owner_org_name = #{ownerOrgName}



    AND synchronous_id = #{synchronousId}
