mapper.select:
SELECT
    id                 AS id,
    code               AS code,
    waybill_code       AS waybillCode,
    distribution_no    AS distributionNo,
    ticket_no          AS ticketNo,
    goods_name         AS goodsName,
    ttl_weight         AS ttlWeight,
    ttl_weight_unit    AS ttlWeightUnit,
    ttl_volume         AS ttlVolume,
    ttl_volume_unit    AS ttlVolumeUnit,
    ttl_number         AS ttlNumber,
    ttl_number_unit    AS ttlNumberUnit,
    wdc_claim          AS wdcClaim,
    wdh_claim          AS wdhClaim,
    whd_claim          AS whdClaim,
    xps_claim          AS xpsClaim,
    other_claim        AS otherClaim,
    claim_description  AS claimDescription,
    status             AS status,
    custom_data        AS customData,
    ticket_tag         AS ticketTag,
    description        AS description,
    remark             AS remark,
    disabled           AS disabled,
    deleted            AS deleted,
    module_code        AS moduleCode,
    creator_user_code  AS creatorUserCode,
    creator_username   AS creatorUsername,
    create_time        AS createTime,
    update_user_code   AS updateUserCode,
    update_username    AS updateUsername,
    update_time        AS updateTime,
    ip                 AS ip,
    operator_longitude AS operatorLongitude,
    operator_latitude  AS operatorLatitude,
    owner_user_code    AS ownerUserCode,
    owner_org_code     AS ownerOrgCode,
    owner_org_name     AS ownerOrgName,
    synchronous_id     AS synchronousId
FROM
    waybill_ticket_detail


    AND id = #{id}



    AND code = #{code}



    AND waybill_code = #{waybillCode}



    AND distribution_no = #{distributionNo}



    AND ticket_no = #{ticketNo}



    AND goods_name = #{goodsName}



    AND ttl_weight = #{ttlWeight}



    AND ttl_weight_unit = #{ttlWeightUnit}



    AND ttl_volume = #{ttlVolume}



    AND ttl_volume_unit = #{ttlVolumeUnit}



    AND ttl_number = #{ttlNumber}



    AND ttl_number_unit = #{ttlNumberUnit}



    AND wdc_claim = #{wdcClaim}



    AND wdh_claim = #{wdhClaim}



    AND whd_claim = #{whdClaim}



    AND xps_claim = #{xpsClaim}



    AND other_claim = #{otherClaim}



    AND claim_description = #{claimDescription}



    AND status = #{status}



    AND custom_data = #{customData}



    AND ticket_tag = #{ticketTag}



    AND description = #{description}



    AND remark = #{remark}



    AND disabled = #{disabled}



    AND deleted = #{deleted}



    AND module_code = #{moduleCode}



    AND creator_user_code = #{creatorUserCode}



    AND creator_username = #{creatorUsername}



    AND create_time = #{createTime}



    AND update_user_code = #{updateUserCode}



    AND update_username = #{updateUsername}



    AND update_time = #{updateTime}



    AND ip = #{ip}



    AND operator_longitude = #{operatorLongitude}



    AND operator_latitude = #{operatorLatitude}



    AND owner_user_code = #{ownerUserCode}



    AND owner_org_code = #{ownerOrgCode}



    AND owner_org_name = #{ownerOrgName}



    AND synchronous_id = #{synchronousId}
